import React, { useState, useEffect } from 'react';

const App = () => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  useEffect(() => {
    const reveal = () => {
      const reveals = document.querySelectorAll(".reveal");
      reveals.forEach(el => {
        const windowHeight = window.innerHeight;
        const elementTop = el.getBoundingClientRect().top;
        if (elementTop < windowHeight - 150) el.classList.add("active");
      });
    };
    window.addEventListener("scroll", reveal);
    reveal();
    return () => window.removeEventListener("scroll", reveal);
  }, []);

  const goToHome = () => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
    window.history.replaceState(null, null, window.location.pathname);
  };

  return (
    <div className="text-slate-900 min-h-screen text-pretty bg-[#fdfcf7]">
      {/* Navigation */}
      <nav className="fixed top-0 w-full z-50 bg-white/80 backdrop-blur-lg border-b border-slate-100">
        <div className="max-w-7xl mx-auto px-6 h-20 flex items-center justify-between">
          <div className="flex items-center space-x-2 cursor-pointer" onClick={goToHome}>
            <div className="w-8 h-8 bg-emerald-500 rounded-full flex items-center justify-center">
              <div className="w-3 h-3 bg-white rounded-full"></div>
            </div>
            <span className="text-2xl font-bold tracking-tight">Operly</span>
          </div>
          <div className="hidden md:flex items-center space-x-10 text-sm font-semibold uppercase tracking-wide text-slate-500">
            <a href="#services" className="nav-link">Services</a>
            <a href="#packages" className="nav-link">Pricing</a>
            <a href="#about" className="nav-link">Our Story</a>
            <a href="#contact" className="btn-vibrant px-6 py-3 rounded-full text-white">Book Call</a>
          </div>
        </div>
      </nav>

      <main id="home" className="pt-20">
        {/* Hero Section */}
        <section className="max-w-7xl mx-auto px-6 py-20 md:py-32 grid md:grid-cols-2 gap-16 items-center">
          <div className="reveal active">
            <span className="inline-block px-4 py-1 rounded-full bg-emerald-100 text-emerald-700 text-xs font-bold uppercase tracking-widest mb-6">Premium Support for Founders</span>
            <h1 className="text-5xl md:text-7xl font-medium mb-8 leading-[1.1]">Your business, <br/><span className="italic serif">run elegantly.</span></h1>
            <p className="text-xl text-slate-600 mb-10 max-w-lg">Operly handles the complex operations that steal your focus. We don't just "do tasks"—we own outcomes.</p>
            <a href="#contact" className="btn-vibrant inline-block px-10 py-5 rounded-full text-lg font-bold shadow-2xl">Book a Consultation</a>
          </div>
          <div className="relative group reveal active">
            {/* Status Badge */}
            <div className="absolute -bottom-6 -left-6 z-20 glass-card p-6 rounded-2xl hidden lg:block shadow-xl border border-white/50 animate-bounce-slow">
              <div className="flex items-center space-x-3">
                <div className="w-10 h-10 bg-emerald-500 rounded-full flex items-center justify-center text-white font-bold">✓</div>
                <div>
                  <p className="text-[10px] font-bold uppercase tracking-tighter text-slate-400">Status</p>
                  <p className="text-sm font-bold text-slate-900">Operations Balanced</p>
                </div>
              </div>
            </div>
            <img src="https://images.unsplash.com/photo-1497215728101-856f4ea42174?auto=format&fit=crop&q=80&w=1000" className="img-mask shadow-2xl w-full object-cover aspect-[4/5] relative z-10" alt="Workspace" />
          </div>
        </section>

        {/* Our Story Section */}
        <section id="about" className="max-w-7xl mx-auto px-6 py-32 border-t border-slate-100">
            <div className="grid md:grid-cols-2 gap-24 items-center">
                <div className="reveal">
                    <img src="https://images.unsplash.com/photo-1522071820081-009f0129c71c?auto=format&fit=crop&q=80&w=1000" className="rounded-[60px] shadow-2xl grayscale hover:grayscale-0 transition duration-1000 w-full object-cover aspect-square" alt="Team" />
                </div>
                <div className="reveal">
                    <h2 className="text-5xl font-medium mb-8 italic serif uppercase tracking-tight"><span className="text-emerald-600">Operly</span> is founder-led.</h2>
                    <div className="space-y-6 text-lg text-slate-600 font-medium leading-relaxed">
                        <p>Operly is a founder-led Virtual Assistant agency based in Canada serving clients globally. We relieve operational burdens for founders, coaches, and service providers, allowing them to focus on growth.</p>
                        <p>We believe in structure, predictability, and the freedom that comes from knowing your operations are in expert hands.</p>
                    </div>
                </div>
            </div>
        </section>
      </main>
    </div>
  );
};

export default App;
